mapper.update:
 	UPDATE attendance_ch
	SET check_out = NOW()
	WHERE emp_id = #{emp_id}
	  AND check_in = (
	      SELECT check_in
	      FROM (
	          SELECT check_in
	          FROM attendance_ch
	          WHERE emp_id = #{emp_id}
	            AND check_in IS NOT NULL
	          ORDER BY check_in DESC
	          LIMIT 1
	      ) AS latest 
	  )
		LIMIT 1;

UPDATE attendance_ch
SET working_time = TIMESTAMPDIFF(MINUTE, check_in, check_out)
WHERE emp_id = #{emp_id} 
AND check_out IS NOT NULL;

mapper.select:
SELECT working_time 
FROM attendance_ch 
WHERE emp_id = #{emp_id} 
AND check_out IS NOT NULL 
ORDER BY check_in DESC 
LIMIT 1;
